import { useState } from "react";
import "./App.css";

function App() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [gender, setGender] = useState("Select Gender");
  const [number, setNumber] = useState("");
  const [registrationnumber, setRegistrationNumber] = useState("");

  const [emptyError, setEmptyError] = useState("");
  const [nameError, setNameError] = useState("");

  const [emailError, setEmailError] = useState("");
  const [genderError, setGenderError] = useState("");
  const [phoneNumberError, setPhoneNumberError] = useState("");
  const [registrationNumberError, setRegistrationNumberError] = useState("");
  const [username, setUserName] = useState("");

  const validate = () => {
    if (
      name === "" ||
      email === "" ||
      gender === "" ||
      number === "" ||
      registrationnumber === ""
    ) {
      setEmptyError("All fields are mandatory");
      setUserName("");
      return false;
    }
    if (!name.match(/^[A-Za-z0-9- ]+$/)) {
      setNameError("Name is not alphanumeric");
      setUserName("");
      return false;
    }
    if (!email.includes("@")) {
      setEmailError("must contain @");
      setUserName("");
      return false;
    }
    if (!gender.match(/male|female|other/i)) {
      setGenderError("please identify as male,female or other");
      setUserName("");
      return false;
    }

    if (number.length !== 10) {
      setPhoneNumberError("Phone Number Must contain 10 digit ");
      setUserName("");
      if (!number.match(/^[0-9]+$/)) {
        setPhoneNumberError("Phone Number Must contain only numbers");
        setUserName("");
        return false;
      }
    }
    if (registrationnumber.length !== 8) {
      setRegistrationNumberError("Registration Number must contain 8 digits");
      setUserName("");
      return false;
    }

    return true;
  };

  const resetDefaultError = () => {
    setEmptyError("");
    setNameError("");
    setEmailError("");
    setGenderError("");
    setPhoneNumberError("");
    setRegistrationNumberError("");
  };

  const hadleSubmit = (e) => {
    e.preventDefault();
    resetDefaultError();
    const isValid = validate();
    if (isValid) {
      setName("");
      setEmail("");
      setNumber("");
      setGender("Select Gender");
      setRegistrationNumber("");
      resetDefaultError();
      setUserName(name)
    }
  };
  return (
    <div className="Maincontainer">
      <form className="FormContainer" onSubmit={hadleSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => {
            setName(e.target.value);
          }}
        />
        {nameError}
        <input
          type="text"
          placeholder="Email"
          value={email}
          onChange={(e) => {
            setEmail(e.target.value);
          }}
        />
        {emailError}
        <select
          name="gender"
          value={gender}
          onChange={(e) => {
            setGender(e.target.value);
          }}
        >
          <option value="Select Gender">Select Gender</option>
          <option value="male">Male</option>
          <option value="female">Female</option>
          <option value="others">Others</option>
        </select>
        {genderError}
        <input
          type="text"
          placeholder="Phone Number"
          value={number}
          onChange={(e) => {
            setNumber(e.target.value);
          }}
        />
        {phoneNumberError}
        <input
          type="text"
          placeholder="Registration Number"
          value={registrationnumber}
          onChange={(e) => {
            setRegistrationNumber(e.target.value);
          }}
        />
        {registrationNumberError}
        <input type="submit" value="Submit" />
        {emptyError}
      </form>

      <div>
        <h2>{username ? "Hello " + username : ""}</h2>
      </div>
    </div>
  );
}

export default App;
