import { useState } from "react";
import "./App.css";

function App() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [gender, setGender] = useState("male");
  const [number, setNumber] = useState("");
  const [registrationnumber, setRegistrationNumber] = useState("");
  const [emptyError, setEmptyError] = useState("");
  const [nameError, setNameError] = useState("");

  const [emailError,setEmailError] = useState("")
  const [genderError, setGenderError] = useState("")
  const [phoneNumberError,setPhoneNumberError] = useState("")
  const [passwordError, setPasswordError] = useState("")

  const validate = () => {
    if (
      name === "" ||
      email === "" ||
      gender === "" ||
      number === "" ||
      registrationnumber === ""
    ) {
      setEmptyError("All fields are mandatory");
      return false;
    }
    if (!name.match(/^[A-Za-z0-9- ]+$/)) {
      setNameError("Name is not alphanumeric");

      return false;
    }
     if (!email.includes("@")) {
      setEmailError("must contain @");
      setUsername("");
      return false;
    }
    if (!gender.match(/male|female|other/i)) {
      setGenderError("please identify as male,female or other");
      setUsername("");
      return false;
    }
    if (!phoneNumber.match(/^[0-9]+$/)) {
      setPhoneNumberError("Phone Number Must contain only numbers");
      setUsername("");
      return false;
    }
    if (password.length < 6) {
      setPasswordError("Password must contain 6 digits");
      setUsername("");
      return false;
    }
    return true;
  };
  const hadleSubmit = (e) => {
    e.preventDefault();
    const isValid = validate();
  };
  return (
    <div className="App">
      <form onSubmit={hadleSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => {
            setName(e.target.value);
          }}
        />
        {nameError}
        <input
          type="text"
          placeholder="Email"
          value={email}
          onChange={(e) => {
            setEmail(e.target.value);
          }}
        />
        <select
          name="gender"
          value={gender}
          onChange={(e) => {
            setGender(e.target.value);
          }}
        >
          <option value="male">Male</option>
          <option value="female">Female</option>
          <option value="others">Others</option>
        </select>
        <input
          type="text"
          placeholder="Phone Number"
          value={number}
          onChange={(e) => {
            setNumber(e.target.value);
          }}
        />
        <input
          type="text"
          placeholder="Registration Number"
          value={registrationnumber}
          onChange={(e) => {
            setRegistrationNumber(e.target.value);
          }}
        />
        {emptyError}
        <input type="submit" value="Submit" />
      </form>
    </div>
  );
}

export default App;
